resources.string:
void setup()
 {
 Serial.begin(9600);
 Serial.println("Hello world from Arduino!");
 }

void loop()
 {
 int incomingByte = 0;
 delay(500);
 String content = "";
 char character;

 while(Serial.available())
 {
 character = Serial.read();
 content.concat(character);
 }

 if (content != "")
 {
 Serial.println("Received data: " + content);
 }
 }
